import React , { useState , useEffect} from 'react';
import axios from 'axios';
import {Link } from "react-router-dom";

 const ListUser = () =>
 {
    
     const[employees,setEmployee]=useState([]);
     
     useEffect(() => {
         
        loadempoyee();
             
         }
     , []);

     const loadempoyee = async() =>{
         const result =await axios.get("http://127.0.0.1:3003/employees");
         
         setEmployee(result.data.reverse())
     };

     const deleteEmployee = async id =>{
         await axios.delete(`http://127.0.0.1:3003/employees/${id}`);
       loadempoyee();
       
     }
     return(<>
        
        <div className="container">
        <div className="py-4">
        <div className="py-4">
        <h2>Employee List</h2>
        <input type="text" placeholder="Search Employee" />
        <Link class="btn btn-primary m-4" to="/addemployee">Create Employee</Link>
       <Link class="btn btn-danger m-2">Delete All</Link>
        <hr />
        </div>
        <table class="table border shadow"  >
  <thead class="thead-warning">
    <tr>
      <th scope="col">Id</th>
      <th scope="col">Firstname</th>
      <th scope="col">Lastname</th>
      <th scope="col">Email</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
      {
        employees.map((employee , index) =>(
        <tr>
        <th scope="col">{index + 1}</th>
       <td> {employee.firstname} </td>
       <td> {employee.lastname} </td>
       <td> {employee.email} </td>
       <td> 
       <Link to={"/addemployee"} class="btn btn-danger" onClick={ () => deleteEmployee(employee.id)}>Delete</Link>
       <Link class="btn btn-outline-secondary m-2" to={`/editemployee/${employee.id}`}>Update</Link>
       <Link class="btn btn-primary m-2">View</Link>
       </td>
       </tr>

     
        ))
      }
  </tbody>
</table>

        </div>
        </div>
        </>
     );
 }
 export default ListUser;
